import React, { useEffect, useState } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import Rooms from './RoomList'; // Update the path as per your project structure
import Reservations from './ReservationList'; // Update the path as per your project structure
import axios from 'axios';

const ReservationCalendar = () => {
    const currentDate = new Date();
    const [date, setDate] = useState(new Date());
    const [reservations, setReservations] = useState([]);
    const [selectedYear, setSelectedYear] = useState(currentDate.getFullYear());
    const [selectedMonth, setSelectedMonth] = useState(currentDate.getMonth() + 1);
    const [viewAllReservations, setViewAllReservations] = useState(true);

    const onChange = date => {
        setDate(date);
        setSelectedMonth(date.getMonth() + 1);
        setSelectedYear(date.getFullYear());
    };

    const handleViewReservations = () => {
        if (viewAllReservations) {
            axios.get(`http://localhost:5000/reservations`)
                .then(response => {
                    setReservations(response.data);
                })
                .catch(error => {
                    console.error('Error fetching reservations:', error);
                });
        } else {
            axios.get(`http://localhost:5000/reservations/sort?year=${selectedYear}&month=${selectedMonth}`)
                .then(response => {
                    setReservations(response.data);
                })
                .catch(error => {
                    console.error('Error fetching reservations:', error);
                });
        }
    };

    useEffect(() => {
        // Fetch reservations initially
        handleViewReservations();
    }, [selectedMonth, selectedYear, viewAllReservations]);

    return (
        <div className='container mt-4'>
            <h2 className='mb-4'>Reservation Calendar</h2>
            <div className='row'>
                <div className='col-md-6'>
                    <Rooms />
                </div>
                <div className='col-md-6'>
                    <Reservations reservations={reservations} />
                </div>
            </div>
            <div className='mt-4'>
                <Calendar
                    onChange={onChange}
                    value={date}
                />
                <button className='btn btn-primary mt-2' onClick={handleViewReservations}>
                    {viewAllReservations ? 'View Specific Reservations' : 'View All Reservations'}
                </button>
            </div>
        </div>
    );
};

export default ReservationCalendar;
