import React, { useState, useEffect } from 'react';
import axios from 'axios';
import RoomComp from './RoomComp';
import ReservationComp from './ReservationComp';

const CalendarScreen = () => {
    const [rooms, setRooms] = useState([]);
    const [reservations, setReservations] = useState([]);

    useEffect(() => {
        // Fetch rooms and reservations data from the backend 
        axios.get('/rooms')
            .then(response => {
                setRooms(response.data);
            })
            .catch(error => {
                console.error('Error fetching rooms:', error);
            });

        axios.get('/reservations')
            .then(response => {
                setReservations(response.data);
            })
            .catch(error => {
                console.error('Error fetching reservations:', error);
            });
    }, []);

    // Render rooms and reservations on the calendar grid

    return (
        <div>
            <h1>Calendar Screen</h1>
            <div className="calendar-container">
                {/* Render room list */}
                <div>
                    <h2>Room List</h2>
                    <ul>
                        {rooms.map(room => (
                            <li key={room._id}>
                                <RoomComp room={room} />
                            </li>
                        ))}
                    </ul>
                </div>

                {/* Display reservations within the calendar */}
                <div>
                    <h2>Reservations</h2>
                    <ul>
                        {reservations.map(reservation => (
                            <li key={reservation._id}>
                                <ReservationComp reservation={reservation} />
                            </li>
                        ))}
                    </ul>
                </div>
            </div>
        </div>
    );
}

export default CalendarScreen;
