import React, { useState } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';

import Rooms from './Rooms'; // Update the path as per your project structure
import Reservations from './Reservations'; // Update the path as per your project structure

const ReservationCalendar = () => {
    const [date, setDate] = useState(new Date());

    const onChange = date => {
        setDate(date);
        // You can add additional logic here to handle date changes
    };

    return (
        <div>
            <h2>Reservation Calendar</h2>
            <Rooms />
            <Reservations />
            <Calendar
                onChange={onChange}
                value={date}
            />
        </div>
    );
};

export default ReservationCalendar;
